function tbNovel:GetTarget2(player, target)
	local count = Map.Things:GetActiveNpcs(g_emNpcRaceType.Wisdom).Count
	local list1 = {}
	for i=0,count - 1,1 do
		local npc = Map.Things:GetActiveNpcs()[i]
		if npc.Name ~= target.Name and npc.Camp == CS.XiaWorld.Fight.g_emFightCamp.Player and  npc.IsDisciple and npc.Sex ~= player.Sex then
			--內門
			table.insert(list1,npc)
		end
	end

	local random = player.LuaHelper:RandomInt(1,#list1);
	return list1[random]
end
