local base = GameMain:GetMod("NovelBase");
local Story = GameMain:GetMod("Story_001");

function Story:Init()
	self.API = base:new();
	self.API.text_list = nil
end

function Story:GetNovel(index)
	return self.API:GetNovel(index)
end

function Story:GetNovelAll()
	return self.API:GetNovelAll()
end

function Story:SetChapter(index)
	if index > #self.chapter then
	return false
	end
	self.API.text_list = self.chapter[index]
	self.Count = #self.API.text_list
	self.API.Count = self.Count
	return true
end


