me:AddSchoolRelation(170,1000);

me:AddMsg("门派答应了[NAME]的请求，谁知道那猪精一招手，不知道从那冒出数十名精壮妖修大汉，与门内女子大战三百回合，良久[NAME]和众女们筋疲力尽的倒在白浆之中。");
local targets = Map.Things:GetNpcsLua(
function(n)
	return n.IsPlayerThing and n.Sex == CS.XiaWorld.g_emNpcSex.Female and n.IsAlive and n:HasSpecialFlag(g_emNpcSpecailFlag.MapExploring) == false and n:HasSpecialFlag(g_emNpcSpecailFlag.AuctionPunish) == false and n.IsSmartRace and n.PropertyMgr:CheckFeature("Pogua") ;
end);
for i = 0, targets.Count-1, 1 do
local target = targets[i];
	if target ~= nil then
		target.PropertyMgr:AddFeature("Tianyuannvquan");
		target.PropertyMgr:AddFeature("Huaitai_zhu");
		target:AddModifier("Shouyun")
		me:AddMsg(target.Name.."已经受孕");
	end
end

me:AddMsg("门派拒绝了[NAME]的请求，但告诉了猪精去天元村的方向，[NAME]追上那猪精说到：门派冷血可姐姐我并不无情，他们不帮，我帮！说罢便褪去衣裳，就要替妖族延续香火，谁知道那猪精一招手，不知道从那冒出数十名精壮妖修大汉，与[NAME]玩起了多人运动，良久[NAME]筋疲力尽的倒在白浆之中。");
me.npcObj.PropertyMgr:AddFeature("Tianyuannvquan");
me.npcObj.PropertyMgr:AddFeature("Huaitai_zhu");
me.npcObj:AddModifier("Shouyun")

xueban = 0
local targets = Map.Things:GetNpcsLua(
function(n)
	return n.IsPlayerThing and n.Sex == CS.XiaWorld.g_emNpcSex.Female and n.IsAlive and n:HasSpecialFlag(g_emNpcSpecailFlag.MapExploring) == false and n:HasSpecialFlag(g_emNpcSpecailFlag.AuctionPunish) == false and n.RaceDefName == "Human" and n.PropertyMgr.RelationData.m_mapRelationShips:ContainsKey("Spouse") == false ;
end);
for i = 0, targets.Count-1, 1 do
local target = targets[i];
	if target ~= nil then
		if xueban > 2 then
		else
		me.npcObj.PropertyMgr.RelationData:AddRelationShip(target,"Spouse");
		me:AddMsg("身为人族未婚女子的"..target.Name.."被"..SchoolMgr.Name.."以不同意就开革出宗门的胁迫，强行分配给[NAME]成为它的修炼伙伴。");
		xueban = xueban + 1
		end
	end
end
me:AddMsg("经过了研究和探讨，"..SchoolMgr.Name.."觉得[NAME]的说法确实有理有据，且出于种族差异，决定在本门中挑选了"..xueban.."名未婚人族女弟子成为[NAME]的修炼伙伴。");

me:AddMsg("经过了研究和探讨，"..SchoolMgr.Name.."打断了[NAME]的五肢，将其赶出了"..SchoolMgr.Name.."自生自灭，却不曾想[NAME]一被赶出宗门，便去了本地最大的同族阿訇庙中哭诉，而本地阿訇也带着一大票族人打着“"..SchoolMgr.Name.."欺辱少数妖族”的名义，杀上了"..SchoolMgr.Name.."。");
ThingMgr:RemoveThing(me.npcObj,false,true)
renshu = me:RandomInt(15,25);
while renshu > 0 do
	local npc = CS.XiaWorld.NpcRandomMechine.RandomNpc(me.npcObj.RaceDefName,g_emNpcSex.Male);
	CS.XiaWorld.NpcMgr.Instance:AddNpc(npc,npc.LuaHelper:RandomInt(240,280),Map,CS.XiaWorld.Fight.g_emFightCamp.Enemy);
	CS.XiaWorld.ThingMgr.Instance:EquptNpc(npc,npc.LuaHelper:RandomInt(1,10),CS.XiaWorld.g_emNpcRichLable.Richest);
	npc.FightBody.AttackWait = 1;
	npc.FightBody.AttackTime = 10000;
	npc.FightBody.AutoNext = true;
	npc.FightBody.IsAttacker = true;
	renshu = renshu - 1
end

if me.npcObj.PropertyMgr:CheckFeature("YangBody") then
me:AddMsg("[NAME]心想对方丑陋又如何，只要肯给钱陪她一晚又何妨，但[NAME]没想到这一晚几乎去了他半条命，要不是自己体质好，可能就再也醒不过来，不过能拿到钱还是好的。\n[NAME]：获得白银50万两");
me.npcObj:AddModifier("Qian");
